import React, { useState } from "react";
import { useSelector } from "react-redux";
import Head from "next/head";
import Container from "../components/container";
import styles from "../styles/index.module.scss";

const Test = () => {
  //Main Hooks
  //Nombre del selector = useSelector(state = state.nombreDelReducer.NombreDelObjetoDentroDelStateDondeSeInyectaLaInfoNueva)
  const weatherSelector = useSelector(
    (state) => state.weatherInfo.weatherInformation
  );
  const oneSelector = useSelector((state) => state.oneReducer.oneApi);

  let unitImperial = "°C";
  if(weather === false) {

  }
  let details = "";
  if (oneSelector.current) {
    let weatherIconOne = `http://openweathermap.org/img/wn/${oneSelector.daily[0].weather[0].icon}.png`;
    let weatherIconTwo = `http://openweathermap.org/img/wn/${oneSelector.daily[1].weather[0].icon}.png`;
    let weatherIconThree = `http://openweathermap.org/img/wn/${oneSelector.daily[2].weather[0].icon}.png`;
    let weatherIconFour = `http://openweathermap.org/img/wn/${oneSelector.daily[3].weather[0].icon}.png`;
    let weatherIconFive = `http://openweathermap.org/img/wn/${oneSelector.daily[4].weather[0].icon}.png`;
    details = (
      <div>
        <div className={styles.divWeather}>
          <div>
            <h1>
              {weatherSelector.name}, {weatherSelector.sys.country}
            </h1>
          </div>

          <div className={styles.boxDetails}>
            <h3>{Math.round(weatherSelector.main.temp)}</h3>
            <h3>{weatherSelector.weather[0].description}</h3>
          </div>
        </div>
        <div className={styles.boxOrder}>
          <div className={styles.box}>
            <h2>Day</h2>
            <img className={styles.iconStyle} src={weatherIconOne} alt="" />
            <p>Feels Like: {Math.round(oneSelector.daily[0].feels_like.day)}</p>
            <p>Temp. Min: {Math.round(oneSelector.daily[0].temp.min)}</p>
            <p>Temp. Max: {Math.round(oneSelector.daily[0].temp.max)}</p>
            <p>Conditions: {oneSelector.daily[0].weather[0].description}</p>
          </div>

          <div className={styles.box}>
            <h2>Day</h2>
            <img className={styles.iconStyle} src={weatherIconOne} alt="" />
            <p>Feels Like: {Math.round(oneSelector.daily[1].feels_like.day)}</p>
            <p>Temp. Min: {Math.round(oneSelector.daily[1].temp.min)}</p>
            <p>Temp. Max: {Math.round(oneSelector.daily[1].temp.max)}</p>
            <p>Conditions: {oneSelector.daily[1].weather[0].description}</p>
          </div>

          <div className={styles.box}>
            <h2>Day</h2>
            <img className={styles.iconStyle} src={weatherIconOne} alt="" />
            <p>Feels Like: {Math.round(oneSelector.daily[2].feels_like.day)}</p>
            <p>Temp. Min: {Math.round(oneSelector.daily[2].temp.min)}</p>
            <p>Temp. Max: {Math.round(oneSelector.daily[2].temp.max)}</p>
            <p>Conditions: {oneSelector.daily[2].weather[0].description}</p>
          </div>

          <div className={styles.box}>
            <h2>Day</h2>
            <img className={styles.iconStyle} src={weatherIconOne} alt="" />
            <p>Feels Like: {Math.round(oneSelector.daily[3].feels_like.day)}</p>
            <p>Temp. Min: {Math.round(oneSelector.daily[3].temp.min)}</p>
            <p>Temp. Max: {Math.round(oneSelector.daily[3].temp.max)}</p>
            <p>Conditions: {oneSelector.daily[3].weather[0].description}</p>
          </div>

          <div className={styles.box}>
            <h2>Day</h2>
            <img className={styles.iconStyle} src={weatherIconOne} alt="" />
            <p>Feels Like: {Math.round(oneSelector.daily[4].feels_like.day)}</p>
            <p>Temp. Min: {Math.round(oneSelector.daily[4].temp.min)}</p>
            <p>Temp. Max: {Math.round(oneSelector.daily[4].temp.max)}</p>
            <p>Conditions: {oneSelector.daily[4].weather[0].description}</p>
          </div>
        </div>
      </div>
    );
  } else {
    details = "Loading...";
  }

  return (
    <div>
      {/*Head es el titulo de la pagina en el browser */}
      <Head>
        <title>Next Project - Test</title>
      </Head>
      <Container>
        <h1>{details}</h1>
      </Container>
    </div>
  );
};

export default Test;
