import React from "react";
import { useSelector } from "react-redux";
import Head from "next/head";
import Container from "../components/container";
import styles from "../styles/index.module.scss";

//timestamp converter
function convertTimestamp(timestamp) {
  var d = new Date(timestamp * 1000), // Convert the passed timestamp to milliseconds
    yyyy = d.getFullYear(),
    mm = ("0" + (d.getMonth() + 1)).slice(-2), // Months are zero based. Add leading 0.
    dd = ("0" + d.getDate()).slice(-2), // Add leading 0.
    hh = d.getHours(),
    h = hh,
    min = ("0" + d.getMinutes()).slice(-2), // Add leading 0.
    ampm = "AM",
    time;

  if (hh > 12) {
    h = hh - 12;
    ampm = "PM";
  } else if (hh === 12) {
    h = 12;
    ampm = "PM";
  } else if (hh == 0) {
    h = 12;
  }

  // ie: 2014-03-24, 3:00 PM
  time = h + ":" + min + " " + ampm;
  return time;
}

const Test = () => {
  //Main Hooks
  //Nombre del selector = useSelector(state = state.nombreDelReducer.NombreDelObjetoDentroDelStateDondeSeInyectaLaInfoNueva)
  const weatherSelector = useSelector(
    (state) => state.weatherInfo.weatherInformation
  );
  const oneSelector = useSelector((state) => state.oneReducer.oneApi);

  //TimeStamps
  let timeSunrise = "";
  let timeSunset = "";
  if (oneSelector.hourly) {
    timeSunrise = oneSelector.current.sunrise;
    timeSunset = oneSelector.current.sunset;
  } else {
    timeSunrise = "no data";
  }

  //timestamp used
  let convertedSunrise = convertTimestamp(timeSunrise);
  let convertedSunset = convertTimestamp(timeSunset);

  let details = "";
  if (oneSelector.current) {
    details = (
      <div>
        <div className={styles.divWeather}>
          <h1>
            {weatherSelector.name}, {weatherSelector.sys.country}
          </h1>
        </div>
        <div className={styles.boxOrder}>
          <div className={styles.box}>
            <p>Temp. Min: {oneSelector.daily[0].temp.min}</p>
            <p>Temp. Max: {oneSelector.daily[0].temp.max}</p>
            <p>Conditions: {oneSelector.daily[0].weather[0].description}</p>
            <p>Clouds: {oneSelector.daily[0].clouds}%</p>
          </div>
          <div className={styles.box}>box</div>
          <div className={styles.box}>box</div>
          <div className={styles.box}>box</div>
          <div className={styles.box}>box</div>
        </div>
      </div>
    );
  } else {
    details = "no data";
  }

  return (
    <div>
      {/*Head es el titulo de la pagina en el browser */}
      <Head>
        <title>Next Project - Test</title>
      </Head>
      <Container>
        <h1>Test</h1>
        <h6>{details}</h6>
      </Container>
    </div>
  );
};

export default Test;
