import React, {Fragment} from 'react';
import { useSelector } from 'react-redux';

import styled from 'styled-components';
import {Meter2} from '@styled-icons/icomoon/Meter2';
import {TempCold} from '@styled-icons/remix-line/TempCold'
import {ContrastDrop2} from '@styled-icons/remix-line/ContrastDrop2'
import {Windy} from '@styled-icons/remix-line/Windy'
import {Cloud} from '@styled-icons/remix-line/Cloud'
import {Droplet} from '@styled-icons/icomoon/Droplet';
import {Eye} from '@styled-icons/remix-fill/Eye';
import {Sun} from '@styled-icons/remix-line/Sun';
import {WeatherNight} from '@styled-icons/typicons/WeatherNight';
import Head from 'next/head';
import Container from '../components/container';


//timestamp converter
function convertTimestamp(timestamp) {
    var d = new Date(timestamp * 1000), // Convert the passed timestamp to milliseconds
        yyyy = d.getFullYear(),
        mm = ('0' + (d.getMonth() + 1)).slice(-2),  // Months are zero based. Add leading 0.
        dd = ('0' + d.getDate()).slice(-2),         // Add leading 0.
        hh = d.getHours(),
        h = hh,
        min = ('0' + d.getMinutes()).slice(-2),     // Add leading 0.
        ampm = 'AM',
        time;

    if (hh > 12) {
        h = hh - 12;
        ampm = 'PM';
    } else if (hh === 12) {
        h = 12;
        ampm = 'PM';
    } else if (hh == 0) {
        h = 12;
    }

    // ie: 2014-03-24, 3:00 PM
    time = h + ':' + min + ' ' + ampm;
    return time;
  }




  //Icons


  const TemperatureL = styled(TempCold)`
    color: #161616;

    width: 2em;
    margin-right: .8em;
  `
  const TheGauge = styled(Meter2)`
    color: #161616;
    width: 2em;
    margin-right: .8em;

  `
  const Humidity = styled(ContrastDrop2)`
    color: #161616;
    width: 2em;
    margin-right: .8em;

  `
  const TheWind = styled(Windy)`
    color: #161616;
    width: 2em;
    margin-right: .8em;

  `
  const Clouds = styled(Cloud)`
    color: #161616;
    width: 2em;
    margin-right: .8em;
  `

  const Visible = styled(Eye)`
    color: #161616;
    width: 2em;
    margin-right: .8em;
  `

  const TheSunrise = styled(Sun)`
    color: #161616;
    width: 2em;
    margin-right: .8em;
  `
  const TheSunset = styled(WeatherNight)`
    color: #161616;
    width: 2em;
    margin-right: .8em;
  `


const Test = () => {
    //Main Hooks
  const weatherSelector = useSelector(state => state.weatherInfo.weatherInformation);
  const oneSelector = useSelector(state => state.oneInfo.oneApi);
  let visibility = Math.round((weatherSelector.visibility) / 100) / 10;

  //TimeStamps
  let timeSunrise = '';
let timeSunset = '';
if(oneSelector.hourly) {
    timeSunrise = oneSelector.current.sunrise;
    timeSunset = oneSelector.current.sunset;
} else {
  timeSunrise = 'no data'
}

//timestamp used
let convertedSunrise = convertTimestamp(timeSunrise)
let convertedSunset = convertTimestamp(timeSunset)


      let details = "";
    if(oneSelector.current) {
        details = 
        <div className='bigList'>

          <div className='mainListComponent'>


            <div className='firstMainList'>

              <ListGroup  variant="flush">
                <ListGroup.Item className='itemListStyle'>
                    <div className='itemList'>
                    <TemperatureL />
                    <h6>Feels Like: {Math.round(weatherSelector.main.feels_like)} °C</h6>
                    </div>

                </ListGroup.Item>

                <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <Humidity />
                    <h6>Humidity:  {weatherSelector.main.humidity}%</h6>
                  </div>
                </ListGroup.Item>

                <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <TheWind />
                    <h6>Wind: {weatherSelector.wind.speed} km/h</h6>
                  </div>
                </ListGroup.Item>

                <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <Clouds />
                    <h6>Clouds: {weatherSelector.clouds.all}%</h6>
                  </div>
                </ListGroup.Item>

              </ListGroup>
            </div>


            <div>
                <ListGroup  variant="flush">


                  <ListGroup.Item className='itemListStyle'>
                      <div className='itemList'>
                      <TheSunrise />
                      <h6>Sunrise: {convertedSunrise}</h6>
                      </div>
                    </ListGroup.Item>

                      <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <TheSunset />
                    <h6>Sunset: {convertedSunset}</h6>
                  </div>
                </ListGroup.Item>

                  <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <TheGauge />
                    <h6>Pressure: {weatherSelector.main.pressure} hPa</h6>
                  </div>
                </ListGroup.Item>


                <ListGroup.Item className='itemListStyle'>
                  <div className='itemList'>
                    <Visible />
                    <h6>Visibility: {visibility} km</h6>
                  </div>
                  </ListGroup.Item>
                </ListGroup>
              </div>


          </div>

            <div className='laterStyle'>

              <button className='mainCta'>Hourly</button>
              <button className='mainCta'>Daily</button>

            </div>

        </div>
    }else {
        details = 
        <div>
          <p>Loading</p>
        </div>
    }



    return (
        <Fragment>
            {details}

            </Fragment>

    )
}

export default Test;
