import React from "react";
import { useSelector } from "react-redux";
import Head from "next/head";
import Container from "../components/container";
import styles from "../styles/index.module.scss";

//timestamp converter
function convertTimestamp(timestamp) {
  var d = new Date(timestamp * 1000), // Convert the passed timestamp to milliseconds
    yyyy = d.getFullYear(),
    mm = ("0" + (d.getMonth() + 1)).slice(-2), // Months are zero based. Add leading 0.
    dd = ("0" + d.getDate()).slice(-2), // Add leading 0.
    hh = d.getHours(),
    h = hh,
    min = ("0" + d.getMinutes()).slice(-2), // Add leading 0.
    ampm = "AM",
    time;

  if (hh > 12) {
    h = hh - 12;
    ampm = "PM";
  } else if (hh === 12) {
    h = 12;
    ampm = "PM";
  } else if (hh == 0) {
    h = 12;
  }

  // ie: 2014-03-24, 3:00 PM
  time = h + ":" + min + " " + ampm;
  return time;
}

var options = {
  weekday: "long",
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "2-digit",
  minute: "2-digit",
  second: "2-digit",
  hour12: false,
};
var prnDt = "Printed on " + new Date().toLocaleTimeString("en-us", options);

console.log(prnDt);
var prnDt =
  "Printed on " + new Date().toLocaleDateString("en-us", { weekday: "long" });
console.log(prnDt);

const Test = () => {
  //Main Hooks
  //Nombre del selector = useSelector(state = state.nombreDelReducer.NombreDelObjetoDentroDelStateDondeSeInyectaLaInfoNueva)
  const weatherSelector = useSelector(
    (state) => state.weatherInfo.weatherInformation
  );
  const oneSelector = useSelector((state) => state.oneReducer.oneApi);

  //TimeStamps
  let timeSunrise = "";
  let timeSunset = "";
  if (oneSelector.hourly) {
    timeSunrise = oneSelector.current.sunrise;
    timeSunset = oneSelector.current.sunset;
  } else {
    timeSunrise = "no data";
  }

  //timestamp used
  let convertedSunrise = convertTimestamp(timeSunrise);
  let convertedSunset = convertTimestamp(timeSunset);

  let details = "";
  if (oneSelector.current) {
    let weatherIconOne = `http://openweathermap.org/img/wn/${oneSelector.daily[0].weather[0].icon}.png`;
    details = (
      <div>
        <div className={styles.divWeather}>
          <h1>
            {weatherSelector.name}, {weatherSelector.sys.country}
          </h1>
        </div>
        <div className={styles.boxOrder}>
          {oneSelector.daily.slice(0, 5).map((weather) => (
            <div key={weather}>
              <div className={styles.box}>
                <h2>Day</h2>
                <img className={styles.iconStyle} src={weatherIconOne} alt="" />
                <p>Feels Like: {weather.feels_like.day}</p>
                <p>Temp. Min: {weather.temp.min}</p>
                <p>Temp. Max: {weather.temp.max}</p>
                <p>Conditions: {weather.weather[0].description}</p>
                <p>Clouds: {weather.clouds} %</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    details = "no data";
  }

  return (
    <div>
      {/*Head es el titulo de la pagina en el browser */}
      <Head>
        <title>Next Project - Test</title>
      </Head>
      <Container>
        <h1>Test</h1>
        <h6>{details}</h6>
      </Container>
    </div>
  );
};

export default Test;
